def solve_2(data):
    data = [(i, int(bus_id)) for i, bus_id in enumerate(data[1].split(',')) if bus_id != 'x']
    jump = data[0][1]
    time_stamp = 0
    for delta, bus_id in data[1:]:
        while (time_stamp + delta) % bus_id != 0:
            time_stamp += jump
        jump *= bus_id
    return time_stamp

import fileinput


def write(memory, mask, address, value):
    if "X" in mask:
        i = mask.index("X")
        write(memory, mask[:i] + "0" + mask[i+1:], address, value)
        write(memory, mask[:i] + "1" + mask[i+1:], address, value)
    else:
        memory[int(mask, 2) | address] = value


