def solution(numbers, until=2020):
    memory = {n: i + 1 for i, n in enumerate(numbers[:-1])}

    for i in range(len(numbers), until):
        numbers.append(i - memory.get(numbers[-1], i))
        memory[numbers[-2]] = i

    return numbers[-1]


