    valid_seq = set()
    rules = True
    nearby = False
    solution = 0

    for line in input:
        if rules:
            seq = re.findall('\d+', line)
            round = 0
            while round < 3 and len(seq) > 0:
                for _ in range(int(seq[round]),int(seq[round +1]) + 1):
                    valid_seq.add(_)
                round += 2
            sorted(valid_seq)

        elif nearby:
            numbers = line.split(",")
            for number in numbers:
                if int(number) not in valid_seq:
                    solution += int(number)

        if len(line) == 0:
            rules = False

        if line.startswith("nearby"):
            nearby = True
    return solution

